        int target = index;
        int left = 2 * index + 1;
        int right = left + 1;

        if (left length &&
                heap->data[left]->count data[target]->count) {
            target = left;
        }

        if (right length &&
                heap->data[right]->count data[target]->count) {
            target = right;
        }

        if (target == index) {
            break;
        }

        swap(&heap->data[index], &heap->data[target]);
        index = target;
    }

    return result;
}

